mapper.insert:
INSERT INTO community_post (
id, category, title, content, writer, filename
) VALUES (
community_seq.NEXTVAL,
#{category},
#{title},
#{content},
#{writer},
#{filename}
)

mapper.select:
SELECT * FROM (
SELECT a.*, ROWNUM rnum FROM (
SELECT * FROM community_post ORDER BY id DESC
) a
WHERE ROWNUM <= #{end}
)
WHERE rnum >= #{start}

mapper.update:
UPDATE community_post
SET title = #{title},
content = #{content},
category = #{category},
filename = #{filename}
WHERE id = #{id}
